sys_variable_value.value:
(function execute(inputs, outputs) {

  var grReportAll = new GlideRecord('sn_hr_workday_configuration_workday_approval_to_dos');
  grReportAll.setValue('u_job_tracker', '');
  grReportAll.setValue('u_is_complete', true);
  grReportAll.updateMultiple();

  var grMainJob = new GlideRecord(new WorkdayPullToDosHelper().WORKDAY_JOB_TRACKER_TABLE);
  grMainJob.addQuery('sys_id', inputs.job_tracker);
  grMainJob.addQuery('state', 'complete');
  grMainJob.query();
  if (grMainJob.next()) {   
    var active_reports = 0;
    var grReport = new GlideRecord('sn_hr_workday_configuration_workday_approval_to_dos');
    grReport.addQuery('u_active', true);
    grReport.query();
    while(grReport.next()){
      	grReport.setValue('u_job_tracker', grMainJob.sys_id);
      	grReport.setValue('u_is_complete', false);
      	grReport.update();
      	active_reports++;
    }

    if(active_reports > 0) {
    	grMainJob.state = 'running';
    	grMainJob.job_ended_at = '';
    	grMainJob.update();
    }
  }


})(inputs, outputs);

