sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {
if (source.sys_import_state == 'inserted' || source.sys_import_state == 'updated') {
        var wtHelper = new WorkdayApprovalToDosHelperUtilsSNC();
        wtHelper.updateRecordClass(source, 'sn_hr_workday_termination_request_todos');
       updateAdditionalFields(source);
    }
    function updateAdditionalFields(source) {
		var wdIntHelper = new WorkdayPullToDosHelper();
        var grToDoP = new GlideRecord('sn_hr_workday_termination_request_todos');
        grToDoP.addEncodedQuery("u_workday_id=" + source.u_workdayid);
        grToDoP.query();
        grToDoP.u_business_process_type = target.u_business_process_type;
        grToDoP.u_close_position = target.u_close_position;
        grToDoP.u_requested_for = wdIntHelper.getUserId(source.u_employee_id);
        grToDoP.u_position = target.u_position;
        grToDoP.u_primary_reason = target.u_primary_reason;
		grToDoP.u_secondary_reason = target.u_secondary_termination_reasons;
        grToDoP.u_position = target.u_position;
        grToDoP.u_termination_date = target.u_termination_date;
        grToDoP.updateMultiple();
    } 
})(source, map, log, target);
